import React, { useEffect, useState } from 'react';
import { getTickets } from '../api/api';
import TicketCard from './TicketCard';
import GroupByDropdown from './GroupByDropdown';
import SortByDropdown from './SortByDropdown';
import BacklogIcon from '../assets/backlog.svg';
import ToDoIcon from '../assets/to-do.svg';
import InProgressIcon from '../assets/in-progress.svg';
import DoneIcon from '../assets/done.svg';
import CancelledIcon from '../assets/cancelled.svg';

const KanbanBoard = () => {
  const [tickets, setTickets] = useState([]);
  const [groupBy, setGroupBy] = useState('status');
  const [sortBy, setSortBy] = useState('priority');

  useEffect(() => {
    fetchTickets();
  }, []);

  const fetchTickets = async () => {
    try {
      const response = await getTickets();
      setTickets(response.data);
    } catch (error) {
      console.error('Error fetching tickets:', error);
    }
  };

  const handleGroupByChange = (value) => {
    setGroupBy(value);
  };

  const handleSortByChange = (value) => {
    setSortBy(value);
  };

  const groupTickets = () => {
    // Logic to group tickets based on the selected groupBy value
  };

  const sortTickets = (groupedTickets) => {
    // Logic to sort tickets within each group based on the selected sortBy value
  };

  const groupedTickets = groupTickets();
  const sortedTickets = sortTickets(groupedTickets);

  return (
    <div className="kanban-board">
      <div className="board-header">
        <GroupByDropdown onGroupByChange={handleGroupByChange} />
        <SortByDropdown onSortByChange={handleSortByChange} />
      </div>
      <div className="board-columns">
        <div className="column">
          <div className="column-header">
            <img src={BacklogIcon} alt="Backlog Icon" />
            <h2>Backlog</h2>
          </div>
          {/* Render the grouped and sorted tickets for the Backlog column */}
        </div>
        <div className="column">
          <div className="column-header">
            <img src={ToDoIcon} alt="To Do Icon" />
            <h2>To Do</h2>
          </div>
          {/* Render the grouped and sorted tickets for the To Do column */}
        </div>
        <div className="column">
          <div className="column-header">
            <img src={InProgressIcon} alt="In Progress Icon" />
            <h2>In Progress</h2>
          </div>
          {/* Render the grouped and sorted tickets for the In Progress column */}
        </div>
        <div className="column">
          <div className="column-header">
            <img src={DoneIcon} alt="Done Icon" />
            <h2>Done</h2>
          </div>
          {/* Render the grouped and sorted tickets for the Done column */}
        </div>
        <div className="column">
          <div className="column-header">
            <img src={CancelledIcon} alt="Cancelled Icon" />
            <h2>Cancelled</h2>
          </div>
          {/* Render the grouped and sorted tickets for the Cancelled column */}
        </div>
      </div>
    </div>
  );
};

export default KanbanBoard;
