// KanbanBoard.js
import React, { useEffect, useState } from 'react';
import { getTickets, createTicket } from '../api/api';
import TicketCard from './TicketCard';
import Header from './Header';
import AddTicketForm from './AddTicketForm';
import BacklogIcon from '../assets/backlog.svg';
import ToDoIcon from '../assets/to-do.svg';
import InProgressIcon from '../assets/in-progress.svg';
import DoneIcon from '../assets/done.svg';
import CancelledIcon from '../assets/cancelled.svg';

const KanbanBoard = () => {
  const [tickets, setTickets] = useState([]);
  const [groupBy, setGroupBy] = useState('status');
  const [sortBy, setSortBy] = useState('priority');

  useEffect(() => {
    fetchTickets();
  }, []);

  const fetchTickets = async () => {
    try {
      const response = await getTickets();
      setTickets(response.data);
    } catch (error) {
      console.error('Error fetching tickets:', error);
    }
  };

  const handleGroupByChange = (value) => {
    setGroupBy(value);
  };

  const handleSortByChange = (value) => {
    setSortBy(value);
  };

  const handleAddTicket = async (newTicket) => {
    try {
      const response = await createTicket(newTicket);
      setTickets([...tickets, response.data]);
    } catch (error) {
      console.error('Error adding ticket:', error);
    }
  };

  const groupTickets = () => {
    const grouped = {};
    tickets.forEach((ticket) => {
      const groupKey = groupBy === 'status'
        ? ticket.status
        : groupBy === 'user'
        ? ticket.assignee
        : 'priority_' + ticket.priority;

      if (!grouped[groupKey]) grouped[groupKey] = [];
      grouped[groupKey].push(ticket);
    });
    return grouped;
  };

  const sortTickets = (tickets) => {
    return [...tickets].sort((a, b) => {
      if (sortBy === 'priority') return b.priority - a.priority;
      if (sortBy === 'title') return a.title.localeCompare(b.title);
      return 0;
    });
  };

  const groupedTickets = groupTickets();
  const columns = Object.keys(groupedTickets);

  return (
    <div className="kanban-board">
      <Header onGroupByChange={handleGroupByChange} onSortByChange={handleSortByChange} />
      <AddTicketForm onAddTicket={handleAddTicket} />
      <div className="board-columns">
        {columns.map((column) => (
          <div key={column} className="column">
            <div className="column-header">
              {column === 'backlog' && <img src={BacklogIcon} alt="Backlog Icon" />}
              {column === 'to_do' && <img src={ToDoIcon} alt="To Do Icon" />}
              {column === 'in_progress' && <img src={InProgressIcon} alt="In Progress Icon" />}
              {column === 'done' && <img src={DoneIcon} alt="Done Icon" />}
              {column === 'cancelled' && <img src={CancelledIcon} alt="Cancelled Icon" />}
              <h2>{column.replace('_', ' ')}</h2>
            </div>
            {sortTickets(groupedTickets[column]).map((ticket) => (
              <TicketCard key={ticket.id} ticket={ticket} />
            ))}
          </div>
        ))}
      </div>
    </div>
  );
};

export default KanbanBoard;
